<?php
function getip ()
{
	if (getenv('http_client_ip')) {
			$ip = getenv('http_client_ip');
	} else if (getenv('http_x_forwarded_for')) {
			$ip = getenv('http_x_forwarded_for');
	} else if (getenv('remote_addr')) {
			$ip = getenv('remote_addr');
	} else {
			$ip = $_server['remote_addr'];
	}
	return $ip;
}

$key = "sii_marmot_id";

if(!$_COOKIE[$key]){
	
	$domain = "sii.sdo.com"; //设置ip地址

	$ip = getip();
	$now = time();
	$lifetime = $now+3600*24*365*20;
	$value = "$ip.$now";
	$value = sprintf("%s.%u",$value,crc32($value));
	setcookie($key, $value, $lifetime, '/', $domain);
}
